        (0.8386761355510099813e19/0.128413970713633903242e21*c[3] + 0.2224717261773437e16/0.2763180339520776e16*c[4] + 0.5e1/0.6e1*c[6] + c[7]/0.24e2 + 0.280535e6/0.371786e6*c[5])*u[5] + 
