    # uₓₓ[m-5:m] = uₓₓ[m-5:m]/Δx
    # uₓₓ[m-5:m-4]  = -1/Δx*uₓₓ[m-5:m-4]
    uₓₓ[m-5]  = -1/Δx*uₓₓ[m-5]
