    uₓₓ[m-2]    = 48/(43Δx)*(-uₓₓ[m-2])
    uₓₓ[m-1]    = 48/(59Δx)*(-uₓₓ[m-1])
    uₓₓ[m]      = 48/(17Δx)*(-uₓₓ[m] + BₙSu)
    
    uₓₓ
end




